import React, { Component } from "react";
import "./LoginSignup.css";
import { Container } from "react-bootstrap";


export class LoginSignup extends Component {
  constructor(props) {
    super(props);
    this.state = {
      option: 1,
    };
  }

  render() {
    return (
      <Container className="login-signup-container">
        <div className="container">
          <header>
            <div
              className={
                "header-headings " +
                (this.state.option === 1
                  ? "sign-in"
                  : this.state.option === 2
                  ? "sign-up"
                  : "forgot")
              }
            >
              <span>Sign in to your account</span>
              <span>Create an account</span>
              <span>Reset your password</span>
            </div>
          </header>
          <ul className="options">
            <li
              className={this.state.option === 1 ? "active" : ""}
              onClick={() => this.setState({ option: 1 })}
            >
              Sign in
            </li>
            <li
              className={this.state.option === 2 ? "active" : ""}
              onClick={() => this.setState({ option: 2 })}
            >
              Sign up
            </li>
            <li
              className={this.state.option === 3 ? "active" : ""}
              onClick={() => this.setState({ option: 3 })}
            >
              Forgot
            </li>
          </ul>
          <form className="account-form" onSubmit={(evt) => evt.preventDefault()}>
      <div
        className={
          "account-form-fields " +
          (option === 1 ? "sign-in" : option === 2 ? "sign-up" : "forgot")
        }
      >
        <input
          id="username"
          name="username"
          type="text"
          placeholder="Username"
          required
        />
        <input
          id="password"
          name="password"
          type="password"
          placeholder="Password"
          required={option === 1 || option === 2 ? true : false}
          disabled={option === 3 ? true : false}
        />

        <input
          id="name"
          name="name"
          type="text"
          placeholder="Full Name"
          required={option === 2 ? true : false}
          disabled={option === 1 || option === 3 ? true : false}
        />
      </div>
      <button className="btn-submit-form" type="submit" onClick={this.handleSubmit(option)}>
        {option === 1 ? "Sign in" : option === 2 ? "Sign up" : "Reset password"}
      </button>
    </form>
          <footer>© 2022 Dev334. All rights reserved.</footer>
        </div>
      </Container>
    );
  }
}

export default LoginSignup;
