import axios from "axios";
require("dotenv").config();

class Playlist {
  constructor() {
    this.getVideoItems = this.getVideoItems.bind(this);
  }

  getVideoItems(playlistId) {
    const link = process.env.BASE_URL + "/playlist/get/info";
    return axios({
      url: link,
      method: "GET",
      params: {
        playlist_id: playlistId,
      },
    });
  }

  setVideoWatched(videoId){
    const link = "http://localhost:3001/playlist/add/video";
  }

}

export default new Playlist();
