import React, { Component } from "react";
import { Col, Container, Row, Card, Image } from "react-bootstrap";
import "./PlaylistItem.css";
import ProgressBar from "react-progressbar.js"

export default class PlaylistItem extends Component {
  constructor(props) {
    super(props);
  }

  render() {
    console.log(this.props);

    var bar = new ProgressBar.Line(progress-bar, {
      strokeWidth: 4,
      easing: 'easeInOut',
      duration: 1400,
      color: '#FFEA82',
      trailColor: '#eee',
      trailWidth: 1,
      svgStyle: {width: '100%', height: '100%'},
      from: {color: '#FFEA82'},
      to: {color: '#ED6A5A'},
      step: (state, bar) => {
        bar.path.setAttribute('stroke', state.color);
      }
    });
    
    bar.animate(1.0);

    return (
      <>
        <Container>
          <Row className="parent-row">
            <Col sm={4}>
              <img
                className="playlist-thumbnail"
                src="https://www.techsmith.com/blog/wp-content/uploads/2021/02/video-thumbnails-hero-1.png"
                alt="playlist thumbnail"
              />
            </Col>
            <Col className="right-column" sm={8}>
              <Row className="add-space">{this.props.name}</Row>
              <Row className="add-space">{this.props.channel}</Row>
              <Row className="add-space"><div className="progress-bar"/></Row>
            </Col>
          </Row>
        </Container>
      </>
    );
  }
}
